#include <iostream>
#include <sstream>
using namespace std;

int main()
{
    string series = R"()";
    char section[13]; 

    int i, j, k,n; 
    long long prod = 0, test;
    for (i = 0; i < series.length(); i++)
    {
        series.copy(section, 13, i);
        test = 1;
        for (j = 0; j < 13; j++)
        {
            n = section[j] - '0';
            test*=n;
        }
            if (test!=0) cout<<test<<'\n';
        if (test > prod)
        {
            prod = test;
        }
        for (k = 0; k < 13; k++)
        {
            section[k] = '\0';
        }
    }

    cout << prod;

    return 0;
}
